=========================================================
* BLK Design System React - v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/blk-design-system-react
* Copyright 2020 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/blk-design-system-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import {


  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  ListGroupItem,
  ListGroup,

  Row,
  Col
} from "reactstrap";
// core components

import IndexNavbar from "components/Navbars/IndexNavbar.js";
import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import PageHeader from "components/PageHeader/PageHeader.js";
import Footer from "components/Footer/Footer.js";
import Accordion from 'react-bootstrap/Accordion';
import "../assets/css/gl.css"
// sections for this page/view
import Basics from "views/IndexSections/Basics.js";
import Navbars from "views/IndexSections/Navbars.js";
import Tabs from "views/IndexSections/Tabs.js";
import Clients from "views/IndexSections/clients.js";
import Pagination from "views/IndexSections/Pagination.js";
import Notifications from "views/IndexSections/Notifications.js";
import Typography from "views/IndexSections/Typography.js";
import JavaScript from "views/IndexSections/JavaScript.js";
import NucleoIcons from "views/IndexSections/NucleoIcons.js";
import product from "views/IndexSections/product.js";
import Signup from "views/IndexSections/Signup.js";
import Examples from "views/IndexSections/Examples.js";
import Download from "views/IndexSections/Download.js";
import { Button,Card, Container, } from "reactstrap";
import AddCategory from "admin/addCategory";
import Firebase from "firebase";
import { MDBNotification } from "mdbreact";
import database from "core/database";
import NavbarPage from "components/Navbars/NavBar";
import JumbotronPage from "./head";
import { MDBCard,MDBContainer, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBRow, MDBCol, MDBIcon } from 'mdbreact';
import Layout from "../components/layout";


class about extends React.Component {
  constructor(props) {
    super(props);


    this.state = {
      categories: []
    };
  }




  render() {
    const { categories } = this.state;
    return (

        <Layout>
             <div className="wrapper">


<div className="main">
<div  className="container-lg text-dark">
    <h1 style={{marginTop:"15vh"}} className="text-dark">END-TO-END SOLAR SOLUTIONS
</h1>
</div>

<MDBContainer>
<MDBRow>

<MDBCol size="12" > <MDBCard style={{backgroundColor:"#ffffff",marginTop:"3vh"}}>

<MDBCardBody cascade className="text-center">
<MDBCardTitle className="text-dark" >
    <h1 className="text-dark" style={{fontFamily:"roboto"}}>VISION</h1>
</MDBCardTitle>
<MDBCardText className="text-dark">To Provide world class solutions in renewable energy, resulting in a better environment for all.</MDBCardText>

</MDBCardBody>
</MDBCard></MDBCol>

</MDBRow>
<MDBRow>

<MDBCol size="12" > <MDBCard style={{backgroundColor:"#ffffff",marginTop:"3vh"}}>

<MDBCardBody cascade className="text-center">
<MDBCardTitle className="text-dark" >
    <h1 className="text-dark" style={{fontFamily:"roboto"}}>MISSION</h1>
</MDBCardTitle>
<MDBCardText className="text-dark">
    <p style={{textAlign:"left"}} className="text-dark">To offer exceptional services in renewable energy, backed by quality products and expertise.</p>
    <p style={{textAlign:"left",marginTop:"3vh"}} className="text-dark">To deliver value and satisfaction to all customers seeking to optimise their energy consumption.</p>
    <p style={{textAlign:"left",marginTop:"3vh"}} className="text-dark">To attain leadership position in the renewable energy sector by delivering the best solution at the right price and ensuring a high return on investment to satisfy every customer.</p>

</MDBCardText>

</MDBCardBody>
</MDBCard></MDBCol>

</MDBRow>
</MDBContainer>




</div>




</div>



        </Layout>
    );
  }
}

export default about;
